// import storeState from './storeState.json';
import {prev} from '../actions';
import {next} from '../actions';

const storeState = [
    {
        "id": "causes",
        "contentH3": "Air Pollution",
        "content": "Lorem 11111"
    },
    {
        "id": "effects",
        "contentH3": "Deforestation",
        "content": "Lorem 222"
    },
    {
        "id": "solutions",
        "contentH3": "Cooling Tower",
        "content": "Lorem 333"
    }
];

let index = 0;

export const reducer =  (state = storeState[index], action) => {
    switch (action.type) {
        case "PREV":
            return {
                state: storeState[index - 1]
            };
        case "NEXT":
            return {
                state: storeState[index + 1]
            };
        default:
            return state;
    }
}
