// import storeState from '../components/main/storeState.json';
import airPollution from '../images/air-pollution.jpg';
import deforestation from '../images/deforestation.jpg';
import coolingTower from '../images/cooling-tower.jpg';

const storeState = [
    {
        index: 0,
        id: "causes",
        contentH3: "Air Pollution",
        content: "Lorem 11111",
        imgSrc: airPollution
    },
    {
        index: 1,
        id: "effects",
        contentH3: "Deforestation",
        content: "Lorem 222",
        imgSrc: deforestation
    },
    {
        index: 2,
        id: "solutions",
        contentH3: "Cooling Tower",
        content: "Lorem 333",
        imgSrc: coolingTower
    }
]


const reducer =  (state = storeState[0], action) => {
    switch (action.type) {
        // case "C":
        //     return state;
        case "E":
            return storeState[1];
        case "S":
            return storeState[2];
        default:
            return state;
    }
};

export default reducer;
