import React, {Component} from 'react';
import {connect} from 'react-redux';
import {c} from '../../actions';
import {e} from '../../actions';
import {s} from '../../actions';
// import airPollution from '../../images/air-pollution.jpg';
// import * as img from '../../images/';

class Section extends Component {
    // onPrev = () => {
    //     this.props.dispatch(prev());
    // }
    //
    // onNext = () => {
    //     this.props.dispatch({
    //         type: "NEXT"
    //     });
    // }

    // constructor(props) {
    //     super(props);
    //     this.onPrev = this.onPrev.bind(this);
    //     this.onNext = this.onNext.bind(this);
    // }
    //
    // onPrev

    render() {
        return (
            <section className={this.props.id}>
                <h2 id={this.props.id}>{this.props.id}</h2>
                <div className="img-container">
                    <div className="img-description">
                        <h3>{this.props.contentH3}</h3>
                        <p>{this.props.content}</p>
                        <p>{this.props.imgScr}</p>
                    </div>
                    <img src={this.props.imgScr} alt={this.props.id + " " + this.props.contentH3} />
                </div>
                <button onClick={this.props.onc}>•</button>
                <button onClick={this.props.one}>•</button>
                <button onClick={this.props.ons}>•</button>
            </section>
        );
    }
}

const root = process.env.PUBLIC_URL;

const mapStateToProps = state => ({
    id: state.id,
    contentH3: state.contentH3,
    content: state.content,
    // imgScr: airPollution
    imgSrc: state.imgSrc
    // imgScr: state.imgScr
});

const mapDispatchToProps = dispatch => ({
    onc: () => dispatch(c()),
    one: () => dispatch(e()),
    ons: () => dispatch(s())
});

// const mapDispatchToProps = dispatch => ({
//     onPrev: () => dispatch(prev()),
//     onNext: () => dispatch(next())
// });
//

export default connect(mapStateToProps, mapDispatchToProps)(Section);
