// import storeState from '../components/main/storeState.json';
import airPollution from '../images/air-pollution.jpg';
import deforestation from '../images/deforestation.jpg';
import coolingTower from '../images/cooling-tower.jpg';

const storeState = [
    {
        index: 0,
        id: "causes",
        contentH3: "Air Pollution",
        content: "Lorem 11111",
        imgSrc: airPollution
    },
    {
        index: 1,
        id: "effects",
        contentH3: "Deforestation",
        content: "Lorem 222",
        imgSrc: deforestation
    },
    {
        index: 2,
        id: "solutions",
        contentH3: "Cooling Tower",
        content: "Lorem 333",
        imgSrc: coolingTower
    }
]


let index = 0;

const reducer =  (state = storeState[1], action) => {
    switch (action.type) {
        case "PREV":
            if (state.index = 0) {
                return state;
            } else {
                return storeState[state.index - 1];
            }
        case "NEXT":
            return storeState[index + 1];
        default:
            return state;
    }
};

export default reducer;
