import React, {Component} from 'react';
import {connect} from 'react-redux';
// import {first} from '../../actions';
// import {second} from '../../actions';
// import {third} from '../../actions';
import {switchSection} from '../../actions';
import {prevSection} from '../../actions';
import {nextSection} from '../../actions';
import {SwitchFilters} from '../../actions';
// import airPollution from '../../images/air-pollution.jpg';
// import * as img from '../../images/';

class Section extends Component {
    render() {
        return (
            <section className={this.props.id}>
                <h2 id={this.props.id}>{this.props.id}</h2>
                <div className="img-container">
                    <div className="img-description">
                        <h3>{this.props.contentH3}</h3>
                        <p>{this.props.content}</p>
                    </div>
                    <img src={this.props.imgSrc} alt={this.props.id + " " + this.props.contentH3} />
                </div>
                <button filter={SwitchFilters.FIRST} onClick={this.props.onSwitch}>•</button>
                <button filter={SwitchFilters.SECOND} onClick={this.props.onSwitch}>•</button>
                <button filter={SwitchFilters.THIRD} onClick={this.props.onSwitch}>•</button>
                <button onClick={this.props.onPrev}>Prev</button>
                <button onClick={this.props.onNext}>Next</button>
            </section>
        );
    }
}

const mapStateToProps = state => ({
    id: state.id,
    contentH3: state.contentH3,
    content: state.content,
    imgSrc: process.env.PUBLIC_URL + state.imgSrc
});

const mapDispatchToProps = (dispatch, ownProps) => ({
    // first: () => dispatch(first()),
    // second: () => dispatch(second()),
    // third: () => dispatch(third())
    onSwitch: () => dispatch(switchSection(ownProps.filter)),
    onPrev: () => dispatch(prevSection()),
    onNext: () => dispatch(nextSection())
});

export default connect(mapStateToProps, mapDispatchToProps)(Section);
