import storeState from '../components/main/storeState.json';

// let index = 1;
const reducer =  (state = storeState[1], action) => {
    switch (action.type) {
        // case "FIRST":
        //     return storeState[0];
        // case "SECOND":
        //     return storeState[1];
        // case "THIRD":
        //     return storeState[2];
        case "PREV":
            return {
                index: state[0].index + 1,
                state: storeState[index]
            };
        case "NEXT":
            return storeState[state[0].index + 1];
        default:
            return state;
    }
};

export default reducer;
