import storeState from '../components/main/storeState.json';

const reducer =  (state = storeState[1], action) => {
    switch (action.type) {
        // case "FIRST":
        //     return storeState[0];
        // case "SECOND":
        //     return storeState[1];
        // case "THIRD":
        //     return storeState[2];
        case "PREV":
            return storeState[storeState[0].index - 1];
        case "NEXT":
            return storeState[storeState[0].index + 1];
        default:
            return state;
    }
};

export default reducer;
