import storeState from '../components/main/storeState.json';

let index = 1;
const reducer =  (state = storeState[index], action) => {
    switch (action.type) {
        // case "FIRST":
        //     return storeState[0];
        // case "SECOND":
        //     return storeState[1];
        // case "THIRD":
        //     return storeState[2];
        case "PREV":
            return storeState[index--];
            console.log(state);
        case "NEXT":
            return storeState[index++];
        default:
            return state;
    }
};

export default reducer;
